import React, {useState} from 'react';
import firebase, {storage} from './firebase';

const firestore = firebase.firestore();

function EditProduct(props) {
    let id = props.location.state.iDListing;
    let listingName = props.location.state.name;
    let listingDes = props.location.state.description;
    let listingPrice = props.location.state.price;
    let listingUrl = props.location.state.url;

    const listingsRef = firestore.collection('listings').doc(id);


    const [nameVal, setFormValue] = useState(listingName);
    const [descriptionVal, setFormValue2] = useState(listingDes);
    const [priceVal, setFormValue3] = useState(listingPrice);
    const [image, setImage] = useState(null);
    const [url, setUrl] = useState(listingUrl);

    //gets image name from stored url
    const getImageName = () => {
        let baseUrl = 'https://firebasestorage.googleapis.com/v0/b/ipproject-27ae8.appspot.com/o/images%2F';
        let imageName = url.replace(baseUrl, '');
        let indexOfEnd = imageName.indexOf('?');
        imageName = imageName.substring(0, indexOfEnd);
        return imageName
    }

    const handleChange = e => {
        if (e.target.files[0]) {
            setImage(e.target.files[0]);
        }
    }

    const uploadImg = () => {
        const uploadImg = storage.ref('images/' + image.name).put(image);

        uploadImg.on("state_changed", snapshot => {
            }, error => {
                console.log(error);
            },
            () => {
                storage.ref('images').child(image.name).getDownloadURL().then(url => {
                    setUrl(url);
                });
            });
    }

    const updateListing = async (e) => {
        e.preventDefault();

        await listingsRef.update({
            name: nameVal,
            description: descriptionVal,
            price: priceVal,
            imgUrl: url
        });
        console.log("product submitted")

        // dunno what this does but I assume it resets the text fields
        setFormValue('');
        setFormValue2('');
        setFormValue3('');
        setImage(null);
        setUrl('');

        //Should redirect back to home page
        window.location.href = "/";
    }

    //deletes image and listing
    const deleteListing = async (e) => {
        try {
            // Obtener el nombre de la imagen
            let imageName = getImageName();
            let storageRef = storage.ref('images').child(imageName);

            // Eliminar la imagen del almacenamiento
            await storageRef.delete();
            console.log(`Imagen ${imageName} eliminada del almacenamiento.`);

            // Eliminar el listado (documento específico)
            await listingsRef.delete();
            console.log("Listado eliminado de la base de datos.");

            // Redirigir al usuario
            window.location.href = "/";
        } catch (error) {
            console.error("Error al eliminar el listado o la imagen:", error);
            alert("Hubo un problema al eliminar el listado. Por favor, intenta nuevamente.");
        }
    };

    return (
        <form className='form' onSubmit={updateListing}>
            <label className='label1'>Nombre: </label><input className="input" type="text" value={nameVal}
                                                           onChange={(e) => setFormValue(e.target.value)}/>
            <label className='label2'>Precio: </label><input className="input" type="text" value={priceVal}
                                                            onChange={(e) => setFormValue3(e.target.value)}/>
            <label className='label1'>Descripcion: </label><textarea className="description"
                                                                     placeholder="Describe your product here"
                                                                     value={descriptionVal}
                                                                     onChange={(e) => setFormValue2(e.target.value)}/>
            <label className='label1'>Subir imagen</label><input className="fileInput" type="file"
                                                                 onChange={handleChange}/>
            <button className='fileUploadButton' type="button" onClick={uploadImg}>Subir imagen</button>
            <img src={url} alt='react logo' className='productImage'/>
            <button className='button' type="submit">Actualizar listado</button>
            <button onClick={deleteListing} type={"button"}>Eliminar listado</button>
        </form>
    )
}

export default EditProduct;
